import React from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import Index from '../screens/Index';
import Smart from '../screens/Smart'
import SmartGo from '../screens/SmartGo'
import Profile from '../screens/Profile'

const Tab = createBottomTabNavigator();

export default function Navigation() {
  return (
    <NavigationContainer>
        <Tab.Navigator 
        initialRouteName='index'
        tabBarOptions={{
          inactiveTintColor:"gray",
          activeTintColor:"blue"
          }}
          screenOptions={({route})=>({
            tabBarIcon:({color})=>{screenIcon(route, color)}
          })}
        >
            <Tab.Screen   name="index"   component={Index}   options={{title:"Inicio"}}/>
            <Tab.Screen   name="smart" component={Smart}   options={{title:"Smart"}}/>
            <Tab.Screen   name="smart-go" component={SmartGo}   options={{title:"SmartGo"}}/>
            <Tab.Screen   name="profile" component={Profile}   options={{title:"Perfil"}}/>
        </Tab.Navigator>
    </NavigationContainer>
  )
}

